(function() {
  if (window.accountModLoaded) return;
  window.accountModLoaded = true;

  const tabButton = document.createElement('button');
  tabButton.textContent = 'Account';
  tabButton.className = 'cobalt-tab';
  tabButton.onclick = () => showAccountTab();
  document.querySelector('.cobalt-tabs').appendChild(tabButton);

  const contentArea = document.querySelector('.cobalt-content');

  function showAccountTab() {
    contentArea.innerHTML = '';
    const user = localStorage.getItem('cobaltUser');
    if (user) {
      const data = JSON.parse(user);
      const welcome = document.createElement('h2');
      welcome.textContent = `Welcome, ${data.username}!`;
      const logoutBtn = document.createElement('button');
      logoutBtn.textContent = 'Log Out';
      logoutBtn.onclick = () => {
        localStorage.removeItem('cobaltUser');
        showAccountTab();
      };
      contentArea.append(welcome, logoutBtn);
    } else {
      const title = document.createElement('h2');
      title.textContent = 'Create or Sign In to Your Account';

      const usernameInput = document.createElement('input');
      usernameInput.placeholder = 'Username';
      usernameInput.style.display = 'block';

      const passwordInput = document.createElement('input');
      passwordInput.placeholder = 'Password';
      passwordInput.type = 'password';
      passwordInput.style.display = 'block';

      const createBtn = document.createElement('button');
      createBtn.textContent = 'Create Account';
      createBtn.onclick = () => {
        const username = usernameInput.value.trim();
        const password = passwordInput.value.trim();
        if (!username || !password) return alert('Fill in both fields');
        localStorage.setItem('cobaltUser', JSON.stringify({ username, password }));
        showAccountTab();
      };

      const signInBtn = document.createElement('button');
      signInBtn.textContent = 'Sign In';
      signInBtn.onclick = () => {
        const stored = localStorage.getItem('cobaltUser');
        if (!stored) return alert('No account found');
        const data = JSON.parse(stored);
        if (usernameInput.value === data.username && passwordInput.value === data.password) {
          showAccountTab();
        } else {
          alert('Incorrect username or password');
        }
      };

      contentArea.append(title, usernameInput, passwordInput, createBtn, signInBtn);
    }
  }
})()
