(function () {
  const STORAGE_KEY = 'cobaltFavorites';

  function getFavorites() {
    return JSON.parse(localStorage.getItem(STORAGE_KEY) || '[]');
  }

  function saveFavorites(favs) {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(favs));
  }

  function isFavorited(name) {
    return getFavorites().includes(name);
  }

  function toggleFavorite(name) {
    const favs = getFavorites();
    const i = favs.indexOf(name);
    if (i >= 0) favs.splice(i, 1);
    else favs.push(name);
    saveFavorites(favs);
    updateAllStars();
    renderFavoritesTab();
  }

  function createStar(name) {
    const star = document.createElement('span');
    star.textContent = 'â­';
    star.className = 'mod-fav-star';
    star.style.marginLeft = '8px';
    star.style.opacity = isFavorited(name) ? '1' : '0.3';
    return star;
  }

  function createFavoriteButton(name) {
    const btn = document.createElement('button');
    btn.textContent = isFavorited(name) ? 'Unfavorite' : 'Favorite';
    btn.className = 'mod-fav-btn';
    btn.style.marginTop = '5px';
    btn.addEventListener('click', e => {
      e.stopPropagation();
      toggleFavorite(name);
    });
    return btn;
  }

  function decorateCard(card, name) {
    const strong = card.querySelector('strong');
    if (!strong || card.querySelector('.mod-fav-star')) return;
    strong.appendChild(createStar(name));
    card.appendChild(createFavoriteButton(name));
  }

  function updateAllStars() {
    document.querySelectorAll('.mod-card').forEach(card => {
      const name = card.querySelector('strong')?.textContent.trim();
      if (!name) return;
      const star = card.querySelector('.mod-fav-star');
      const btn = card.querySelector('.mod-fav-btn');
      if (star) star.style.opacity = isFavorited(name) ? '1' : '0.3';
      if (btn) btn.textContent = isFavorited(name) ? 'Unfavorite' : 'Favorite';
    });
  }

  function renderFavoritesTab() {
    const favs = getFavorites();
    const favContainer = document.getElementById('favorites');
    if (!favContainer) return;
    favContainer.innerHTML = '';

    const allCards = Array.from(document.querySelectorAll('.mod-card'));
    favs.forEach(name => {
      const match = allCards.find(card => {
        const n = card.querySelector('strong')?.textContent.trim();
        return n === name;
      });
      if (match) {
        const clone = match.cloneNode(true);
        decorateCard(clone, name);
        favContainer.appendChild(clone);
      }
    });
  }

  function createFavoritesTab() {
    const tabs = document.getElementById('cobalt-tabs');
    if (!tabs || document.getElementById('favorites-tab')) return;

    const tab = document.createElement('div');
    tab.className = 'cobalt-tab';
    tab.id = 'favorites-tab';
    tab.dataset.tab = 'favorites';
    tab.textContent = 'Favorites';
    tabs.appendChild(tab);

    const content = document.createElement('div');
    content.id = 'favorites';
    content.className = 'cobalt-tab-content';
    document.getElementById('cobalt-sidebar').appendChild(content);

    tab.addEventListener('click', () => {
      document.querySelectorAll('.cobalt-tab').forEach(t => t.classList.remove('active'));
      document.querySelectorAll('.cobalt-tab-content').forEach(c => c.classList.remove('active'));
      tab.classList.add('active');
      content.classList.add('active');
      renderFavoritesTab();
    });
  }

  function decorateAllCards() {
    document.querySelectorAll('.mod-card').forEach(card => {
      const name = card.querySelector('strong')?.textContent.trim();
      if (name) decorateCard(card, name);
    });
  }

  function observeCards() {
    const observer = new MutationObserver(() => {
      decorateAllCards();
      updateAllStars();
    });
    observer.observe(document.body, { childList: true, subtree: true });
  }

  function injectStyles() {
    const style = document.createElement('style');
    style.textContent = `.mod-fav-star { font-size: 1.2em; transition: opacity 0.2s; }`;
    document.head.appendChild(style);
  }

  function init() {
    injectStyles();
    createFavoritesTab();
    decorateAllCards();
    updateAllStars();
    observeCards();
  }

  const wait = setInterval(() => {
    if (document.getElementById('cobalt-tabs') && document.getElementById('cobalt-sidebar')) {
      clearInterval(wait);
      init();
    }
  }, 500);
})();
