(function () {
  const STORAGE_KEY = 'cobaltFavorites';

  function getFavorites() {
    const stored = localStorage.getItem(STORAGE_KEY);
    return stored ? JSON.parse(stored) : [];
  }

  function saveFavorites(favs) {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(favs));
  }

  function toggleFavorite(modName) {
    const favs = getFavorites();
    const index = favs.indexOf(modName);
    if (index >= 0) {
      favs.splice(index, 1);
    } else {
      favs.push(modName);
    }
    saveFavorites(favs);
    updateStars();
    updateFavoritesTab();
  }

  function injectControls(card, modName) {
    const nameEl = card.querySelector('strong');
    if (!nameEl) return;

    // Add star indicator
    let star = card.querySelector('.mod-fav-star');
    if (!star) {
      star = document.createElement('span');
      star.className = 'mod-fav-star';
      star.textContent = 'â­';
      star.style.marginLeft = '8px';
      nameEl.appendChild(star);
    }

    // Add Favorite button
    if (!card.querySelector('.mod-fav-btn')) {
      const favBtn = document.createElement('button');
      favBtn.className = 'mod-fav-btn';
      favBtn.textContent = 'Favorite';
      favBtn.style.marginTop = '5px';
      favBtn.addEventListener('click', e => {
        e.stopPropagation();
        toggleFavorite(modName);
      });
      card.appendChild(favBtn);
    }
  }

  function updateStars() {
    const favs = getFavorites();
    document.querySelectorAll('.mod-card').forEach(card => {
      const nameEl = card.querySelector('strong');
      const modName = nameEl?.textContent.trim();
      if (!modName) return;
      injectControls(card, modName);
      const star = card.querySelector('.mod-fav-star');
      if (star) star.style.opacity = favs.includes(modName) ? '1' : '0.3';
    });
  }

  function updateFavoritesTab() {
    const favContent = document.getElementById('favorites');
    if (!favContent) return;
    const favs = getFavorites();
    favContent.innerHTML = '';

    document.querySelectorAll('.mod-card').forEach(card => {
      const nameEl = card.querySelector('strong');
      const modName = nameEl?.textContent.trim();
      if (favs.includes(modName)) {
        const clone = card.cloneNode(true);
        injectControls(clone, modName);
        const star = clone.querySelector('.mod-fav-star');
        if (star) star.style.opacity = '1';
        favContent.appendChild(clone);
      }
    });
  }

  function createFavoritesTab() {
    const tabBar = document.getElementById('cobalt-tabs');
    if (!tabBar || document.getElementById('favorites-tab')) return;

    const favTab = document.createElement('div');
    favTab.textContent = 'Favorites';
    favTab.className = 'cobalt-tab';
    favTab.id = 'favorites-tab';
    favTab.dataset.tab = 'favorites';
    tabBar.appendChild(favTab);

    const favContent = document.createElement('div');
    favContent.id = 'favorites';
    favContent.className = 'cobalt-tab-content';
    document.getElementById('cobalt-sidebar').appendChild(favContent);

    favTab.addEventListener('click', () => {
      document.querySelectorAll('.cobalt-tab').forEach(t => t.classList.remove('active'));
      document.querySelectorAll('.cobalt-tab-content').forEach(c => c.classList.remove('active'));
      favTab.classList.add('active');
      favContent.classList.add('active');
      updateFavoritesTab();
    });
  }

  function injectStyles() {
    const style = document.createElement('style');
    style.textContent = `
      .mod-fav-star {
        font-size: 1.2em;
        margin-left: 6px;
        transition: opacity 0.2s;
      }
    `;
    document.head.appendChild(style);
  }

  function hookIntoModLoader() {
    if (!window.CobaltHash || typeof CobaltHash.renderMods !== 'function') return;
    const originalRender = CobaltHash.renderMods;
    CobaltHash.renderMods = function () {
      originalRender.call(this);
      updateStars();
      updateFavoritesTab();
    };
  }

  function observeWorkshop() {
    const workshop = document.getElementById('workshop');
    if (!workshop) return;
    const observer = new MutationObserver(() => {
      updateStars();
      updateFavoritesTab();
    });
    observer.observe(workshop, { childList: true, subtree: true });
  }

  function init() {
    injectStyles();
    createFavoritesTab();
    hookIntoModLoader();
    observeWorkshop();
    updateStars();
    updateFavoritesTab();
  }

  const wait = setInterval(() => {
    if (document.getElementById('cobalt-tabs') && document.getElementById('cobalt-sidebar')) {
      clearInterval(wait);
      init();
    }
  }, 500);
})();
