const WorkshopSearchEnhancer = {
  name: "Workshop Search Enhancer",
  description: "Adds a search bar to the Workshop tab to filter mods.",
  run() {
    console.log("Workshop Search Enhancer is running");

    const modList = document.querySelector('.cobalt-mod-list');
    if (!modList || document.querySelector('#cobalt-search-bar')) return;

    const searchBar = document.createElement('input');
    searchBar.id = 'cobalt-search-bar';
    searchBar.type = 'text';
    searchBar.placeholder = 'Search mods...';
    searchBar.style.cssText = `
      width: 100%;
      padding: 8px;
      margin-bottom: 10px;
      font-size: 14px;
      border-radius: 4px;
      border: 1px solid #ccc;
    `;

    modList.parentElement.insertBefore(searchBar, modList);

    searchBar.addEventListener('input', () => {
      const query = searchBar.value.toLowerCase();
      const modCards = modList.querySelectorAll('.cobalt-mod-card');

      modCards.forEach(card => {
        const name = card.querySelector('.cobalt-mod-name')?.textContent.toLowerCase() || '';
        const desc = card.querySelector('.cobalt-mod-description')?.textContent.toLowerCase() || '';
        card.style.display = (name.includes(query) || desc.includes(query)) ? '' : 'none';
      });
    });
  },
  stop() {
    const searchBar = document.querySelector('#cobalt-search-bar');
    if (searchBar) searchBar.remove();
  }
};
