(function () {
  function createUtilitiesTab() {
    const tabs = document.getElementById('cobalt-tabs');
    if (!tabs || document.getElementById('utilities-tab')) return;

    const tab = document.createElement('div');
    tab.className = 'cobalt-tab';
    tab.id = 'utilities-tab';
    tab.dataset.tab = 'utilities';
    tab.textContent = 'Mod Utilities';
    tabs.appendChild(tab);

    const content = document.createElement('div');
    content.id = 'utilities';
    content.className = 'cobalt-tab-content';
    content.innerHTML = `
      <div id="cobalt-utilities-panel">
        <h2>Mod Utilities</h2>
        <p>This space is reserved for utility mods to inject their tools.</p>
      </div>
    `;
    document.getElementById('cobalt-sidebar').appendChild(content);

    tab.onclick = () => {
      document.querySelectorAll('.cobalt-tab').forEach(t => t.classList.remove('active'));
      document.querySelectorAll('.cobalt-tab-content').forEach(c => c.classList.remove('active'));
      tab.classList.add('active');
      content.classList.add('active');
    };
  }

  const wait = setInterval(() => {
    if (document.getElementById('cobalt-tabs') && document.getElementById('cobalt-sidebar')) {
      clearInterval(wait);
      createUtilitiesTab();
    }
  }, 500);
})();
