const ModUtilitiesTab = {
  name: "Mod Utilities Tab",
  description: "Adds a new tab to the sidebar for mod-related utilities.",
  run() {
    const tabBar = document.querySelector('.cobalt-tab-bar');
    const tabContentContainer = document.querySelector('.cobalt-tab-content');

    if (!tabBar || !tabContentContainer) return;

    // Create tab button
    const tabButton = document.createElement('div');
    tabButton.className = 'cobalt-tab';
    tabButton.dataset.tab = 'ModUtilities';
    tabButton.textContent = 'Mod Utilities';
    tabBar.appendChild(tabButton);

    // Create tab content panel
    const tabContent = document.createElement('div');
    tabContent.className = 'cobalt-tab-panel';
    tabContent.dataset.tab = 'ModUtilities';
    tabContent.style.display = 'none';
    tabContent.innerHTML = `
      <div id="cobalt-utilities-panel">
        <h2>Mod Utilities</h2>
        <p>This space is reserved for utility mods to inject their tools.</p>
      </div>
    `;
    tabContentContainer.appendChild(tabContent);

    // Tab switching logic
    tabButton.addEventListener('click', () => {
      document.querySelectorAll('.cobalt-tab-panel').forEach(panel => {
        panel.style.display = 'none';
      });
      document.querySelectorAll('.cobalt-tab').forEach(tab => {
        tab.classList.remove('active');
      });
      tabButton.classList.add('active');
      tabContent.style.display = 'block';
    });
  },
  stop() {
    const tabButton = document.querySelector('.cobalt-tab[data-tab="ModUtilities"]');
    const tabContent = document.querySelector('.cobalt-tab-panel[data-tab="ModUtilities"]');
    if (tabButton) tabButton.remove();
    if (tabContent) tabContent.remove();
  }
};
