const CobaltRestartUtility = {
  name: "Cobalt Restart Utility",
  description: "Restarts the modloader by disabling all mods and refreshing the page.",
  run() {
    console.log("Cobalt Restart triggered");

    // Disable all mods
    const mods = JSON.parse(localStorage.getItem("mods")) || [];
    mods.forEach(mod => {
      try {
        const modObj = eval(mod.code);
        if (modObj && typeof modObj.stop === "function") {
          modObj.stop();
        }
      } catch (e) {
        console.warn(`Failed to stop mod: ${mod.name}`, e);
      }
    });

    // Optionally clear mod state
    // localStorage.removeItem("mods"); // Uncomment if you want a full wipe

    // Refresh the page
    setTimeout(() => {
      location.reload();
    }, 500);
  },
  stop() {
    // No cleanup needed
  }
};
