(function() {
  if (document.getElementById('patch-test-tab')) return;

  const tab = document.createElement('div');
  tab.id = 'patch-test-tab';
  tab.className = 'cobalt-tab';
  tab.style.display = 'none';
  tab.innerHTML = '<h2>Patch_Test</h2><p>This tab is currently empty.</p>';
  document.body.appendChild(tab);

  const button = document.createElement('button');
  button.className = 'cobalt-tab-button';
  button.textContent = 'Patch_Test';
  button.onclick = () => {
    document.querySelectorAll('.cobalt-tab').forEach(t => t.style.display = 'none');
    tab.style.display = 'block';
  };
  document.getElementById('cobalt-sidebar').appendChild(button);

  window.PatchTest_stop = function() {
    tab.remove();
    button.remove();
  };
})();
