(function() {
  if (document.querySelector('[data-tab="patch_test"]')) return;

  const tabButton = document.createElement('div');
  tabButton.className = 'cobalt-tab';
  tabButton.dataset.tab = 'patch_test';
  tabButton.textContent = 'Patch_Test';
  document.getElementById('cobalt-tabs').appendChild(tabButton);

  const tabContent = document.createElement('div');
  tabContent.id = 'patch_test';
  tabContent.className = 'cobalt-tab-content';
  tabContent.innerHTML = '<h2>Patch_Test</h2><p>This tab is currently empty.</p>';
  document.getElementById('cobalt-sidebar').appendChild(tabContent);

  tabButton.addEventListener('click', () => {
    document.querySelectorAll('.cobalt-tab').forEach(t => t.classList.remove('active'));
    document.querySelectorAll('.cobalt-tab-content').forEach(c => c.classList.remove('active'));
    tabButton.classList.add('active');
    tabContent.classList.add('active');
  });

  window.Patch_Test_stop = function() {
    tabButton.remove();
    tabContent.remove();
  };
})();
