document.title = "Pokédex - Generation 1 to 10";

const head = document.head;
const metaCharset = document.createElement("meta");
metaCharset.setAttribute("charset", "UTF-8");
head.appendChild(metaCharset);

const metaViewport = document.createElement("meta");
metaViewport.name = "viewport";
metaViewport.content = "width=device-width, initial-scale=1.0";
head.appendChild(metaViewport);

const metaIE = document.createElement("meta");
metaIE.setAttribute("http-equiv", "X-UA-Compatible");
metaIE.content = "ie=edge";
head.appendChild(metaIE);

const linkStyles = document.createElement("link");
linkStyles.rel = "stylesheet";
linkStyles.href = "styles.css";
head.appendChild(linkStyles);

const body = document.body;

const header = document.createElement("header");
const h1 = document.createElement("h1");
h1.textContent = "Pokédex - Generations 1 to 10";
header.appendChild(h1);

const searchBar = document.createElement("input");
searchBar.type = "text";
searchBar.id = "search-bar";
searchBar.placeholder = "Search Pokémon by Name or ID";
header.appendChild(searchBar);

const cameraButton = document.createElement("button");
cameraButton.id = "open-camera";
cameraButton.textContent = "Open Camera";
header.appendChild(cameraButton);
body.appendChild(header);

const cameraSection = document.createElement("div");
cameraSection.id = "camera-section";
cameraSection.className = "camera-section";

const video = document.createElement("video");
video.id = "video";
video.width = 300;
video.height = 300;
video.autoplay = true;
cameraSection.appendChild(video);

const canvas = document.createElement("canvas");
canvas.id = "canvas";
canvas.width = 300;
canvas.height = 300;
cameraSection.appendChild(canvas);

const captureButton = document.createElement("button");
captureButton.id = "capture-button";
captureButton.textContent = "Capture";
cameraSection.appendChild(captureButton);

const captureMessage = document.createElement("p");
captureMessage.id = "capture-message";
cameraSection.appendChild(captureMessage);
body.appendChild(cameraSection);

const pokemonList = document.createElement("div");
pokemonList.id = "pokemon-list";
pokemonList.className = "pokemon-list";
body.appendChild(pokemonList);

const pokemonDetails = document.createElement("div");
pokemonDetails.id = "pokemon-details";
pokemonDetails.className = "pokemon-details";
body.appendChild(pokemonDetails);

const footer = document.createElement("footer");
const footerText = document.createElement("p");
footerText.innerHTML = "Pokédex App | Powered by <a href=\"https://pokeapi.co/\" target=\"_blank\">PokéAPI</a>";
footer.appendChild(footerText);
body.appendChild(footer);

const script = document.createElement("script");
script.src = "app.js";
document.body.appendChild(script);
