document.title = "Pokédex - Full Stats";

const style = document.createElement("style");
style.textContent = `
  body { font-family: sans-serif; padding: 20px; background: #f8f8ff; }
  h1 { text-align: center; }
  .search-container { display: flex; justify-content: center; gap: 10px; margin-top: 20px; }
  #search-bar { width: 300px; padding: 10px; font-size: 16px; border: 1px solid #ccc; border-radius: 4px; }
  #search-button { padding: 10px 20px; font-size: 16px; background: #4caf50; color: white; border: none; border-radius: 4px; cursor: pointer; }
  #search-button:hover { background: #45a049; }
  #pokemon-list { max-height: 300px; overflow-y: auto; margin-top: 30px; border: 1px solid #ccc; padding: 10px; background: #fff; }
  .pokemon-item { cursor: pointer; padding: 5px; border-bottom: 1px solid #eee; }
  .pokemon-item:hover { background: #eef; }
  #pokemon-details img { max-width: 150px; }
  #pokemon-details { margin-top: 30px; border-top: 2px solid #333; padding-top: 20px; background: #fff; padding: 20px; border-radius: 8px; }
`;
document.head.appendChild(style);

const h1 = document.createElement("h1");
h1.textContent = "Pokédex - Generations 1 to 10";
document.body.appendChild(h1);

const searchContainer = document.createElement("div");
searchContainer.className = "search-container";

const searchBar = document.createElement("input");
searchBar.id = "search-bar";
searchBar.placeholder = "Search Pokémon by Name or ID";
searchContainer.appendChild(searchBar);

const searchButton = document.createElement("button");
searchButton.id = "search-button";
searchButton.textContent = "Search";
searchButton.onclick = () => fetchPokemon(searchBar.value);
searchContainer.appendChild(searchButton);

document.body.appendChild(searchContainer);

const pokemonList = document.createElement("div");
pokemonList.id = "pokemon-list";
document.body.appendChild(pokemonList);

const pokemonDetails = document.createElement("div");
pokemonDetails.id = "pokemon-details";
document.body.appendChild(pokemonDetails);

function fetchPokemon(name) {
  const query = name.toLowerCase().trim();
  if (!query) return;

  fetch(`https://pokeapi.co/api/v2/pokemon/${query}`)
    .then(res => {
      if (!res.ok) throw new Error("Pokémon not found");
      return res.json();
    })
    .then(data => {
      const stats = data.stats.map(s => `<li><strong>${s.stat.name}:</strong> ${s.base_stat}</li>`).join("");
      const types = data.types.map(t => t.type.name).join(", ");
      pokemonDetails.innerHTML = `
        <h2>${data.name.toUpperCase()}</h2>
        <img src="${data.sprites.front_default}" alt="${data.name}">
        <p><strong>ID:</strong> ${data.id}</p>
        <p><strong>Type:</strong> ${types}</p>
        <p><strong>Height:</strong> ${data.height}</p>
        <p><strong>Weight:</strong> ${data.weight}</p>
        <ul>${stats}</ul>
      `;
    })
    .catch(err => {
      pokemonDetails.innerHTML = `<p style='color:red;'>${err.message}</p>`;
    });
}

function loadPokemonList(limit = 151) {
  fetch(`https://pokeapi.co/api/v2/pokemon?limit=${limit}`)
    .then(res => res.json())
    .then(data => {
      data.results.forEach(p => {
        const item = document.createElement("div");
        item.className = "pokemon-item";
        item.textContent = p.name;
        item.onclick = () => fetchPokemon(p.name);
        pokemonList.appendChild(item);
      });
    });
}

loadPokemonList();
