document.addEventListener('DOMContentLoaded', function() {
  const pokemonList = [
    {
      name: 'bulbasaur',
      type: ['grass', 'poison'],
      hp: 45,
      attack: 49,
      defense: 49
    },
    {
      name: 'charmander',
      type: ['fire'],
      hp: 39,
      attack: 52,
      defense: 43
    },
    {
      name: 'squirtle',
      type: ['water'],
      hp: 44,
      attack: 48,
      defense: 65
    },
    {
      name: 'pikachu',
      type: ['electric'],
      hp: 35,
      attack: 55,
      defense: 40
    }
  ];

  const input = document.createElement('input');
  input.id = 'search';
  input.placeholder = 'Enter Pokémon name';

  const button = document.createElement('button');
  button.id = 'searchBtn';
  button.textContent = 'Search';

  const result = document.createElement('div');
  result.id = 'result';

  document.body.appendChild(input);
  document.body.appendChild(button);
  document.body.appendChild(result);

  button.addEventListener('click', function() {
    const query = input.value.toLowerCase().trim();
    const found = pokemonList.find(p => p.name === query);

    if (!found) {
      result.innerHTML = '<p>Pokémon not found in local list.</p>';
      return;
    }

    result.innerHTML = `
      <h2>${found.name.toUpperCase()}</h2>
      <p><strong>Type:</strong> ${found.type.join(', ')}</p>
      <p><strong>HP:</strong> ${found.hp}</p>
      <p><strong>Attack:</strong> ${found.attack}</p>
      <p><strong>Defense:</strong> ${found.defense}</p>
    `;
  });
});
