(function() {
  if (document.getElementById('sticky-notes-button')) return;

  const style = document.createElement('style');
  style.textContent = `.sticky-note {
    position: absolute;
    width: 200px;
    height: 150px;
    background: #fff8a6;
    border: 1px solid #ccc;
    padding: 0;
    font-family: sans-serif;
    resize: both;
    overflow: hidden;
    box-shadow: 2px 2px 10px rgba(0,0,0,0.2);
    z-index: 9999;
  }
  .sticky-note-content {
    width: 100%;
    height: 100%;
    padding: 10px;
    color: #000000;
    background: transparent;
    font-size: 14px;
    overflow: auto;
  }
  .sticky-note-delete {
    position: absolute;
    top: 2px;
    right: 5px;
    font-weight: bold;
    color: #444;
    cursor: pointer;
    z-index: 10000;
  }
  #sticky-notes-button {
    position: fixed;
    bottom: 20px;
    right: 20px;
    background: #0078D7;
    color: white;
    border: none;
    padding: 10px 15px;
    font-weight: bold;
    border-radius: 5px;
    cursor: pointer;
    z-index: 9999;
  }`;
  document.head.appendChild(style);

  const button = document.createElement('button');
  button.id = 'sticky-notes-button';
  button.textContent = 'Add Note';
  document.body.appendChild(button);

  button.addEventListener('click', () => {
    const note = document.createElement('div');
    note.className = 'sticky-note';
    note.style.top = Math.random() * window.innerHeight * 0.6 + 'px';
    note.style.left = Math.random() * window.innerWidth * 0.6 + 'px';

    const deleteBtn = document.createElement('div');
    deleteBtn.className = 'sticky-note-delete';
    deleteBtn.textContent = 'Ã—';
    deleteBtn.addEventListener('click', () => note.remove());
    note.appendChild(deleteBtn);

    const content = document.createElement('div');
    content.className = 'sticky-note-content';
    content.contentEditable = true;
    note.appendChild(content);
    document.body.appendChild(note);

    content.addEventListener('input', () => {
      const text = content.innerText;
      const replaced = text.replace(/(\d+[\+\-\*/]\d+)=/g, (match) => {
        try {
          const expr = match.slice(0, -1);
          const result = eval(expr);
          return expr + '=' + result;
        } catch {
          return match;
        }
      });
      if (replaced !== text) content.innerText = replaced;
    });

    let isDragging = false, offsetX = 0, offsetY = 0;
    note.addEventListener('mousedown', (e) => {
      if (e.target !== content && e.target !== deleteBtn) {
        isDragging = true;
        offsetX = e.clientX - note.getBoundingClientRect().left;
        offsetY = e.clientY - note.getBoundingClientRect().top;
        note.style.cursor = 'grabbing';
      }
    });
    document.addEventListener('mousemove', (e) => {
      if (isDragging) {
        note.style.top = (e.clientY - offsetY) + 'px';
        note.style.left = (e.clientX - offsetX) + 'px';
      }
    });
    document.addEventListener('mouseup', () => {
      isDragging = false;
      note.style.cursor = 'auto';
    });
  });

  window.StickyNotes_stop = function() {
    document.querySelectorAll('.sticky-note').forEach(n => n.remove());
    const btn = document.getElementById('sticky-notes-button');
    if (btn) btn.remove();
  };
})();
