(function() {
  if (window.confettiClickHandler) return;

  window.confettiClickHandler = function(event) {
    const confetti = document.createElement('div');
    confetti.style.position = 'absolute';
    confetti.style.left = `${event.clientX}px`;
    confetti.style.top = `${event.clientY}px`;
    confetti.style.width = '10px';
    confetti.style.height = '10px';
    confetti.style.background = `hsl(${Math.random() * 360}, 100%, 50%)`;
    confetti.style.borderRadius = '50%';
    confetti.style.zIndex = 9999;
    confetti.style.pointerEvents = 'none';
    document.body.appendChild(confetti);
    setTimeout(() => confetti.remove(), 1000);
  };

  document.addEventListener('click', window.confettiClickHandler);
})()
