(function () {
  function waitForWorkshop() {
    const interval = setInterval(() => {
      const modCards = document.querySelectorAll('.mod-card');
      if (modCards.length > 0) {
        clearInterval(interval);
        injectStyles();
        createSearchBar();
      }
    }, 500);
  }

  function createSearchBar() {
    const bar = document.createElement('input');
    bar.type = 'text';
    bar.placeholder = '🔍 Search mods...';
    bar.className = 'workshop-search-bar';
    document.body.appendChild(bar);

    bar.addEventListener('input', () => {
      const query = bar.value.toLowerCase();
      const modCards = document.querySelectorAll('.mod-card');

      modCards.forEach(card => {
        const name = card.querySelector('.mod-name')?.textContent.toLowerCase() || '';
        const author = card.querySelector('.mod-author')?.textContent.toLowerCase() || '';
        const desc = card.querySelector('.mod-description')?.textContent.toLowerCase() || '';

        const match = name.includes(query) || author.includes(query) || desc.includes(query);
        card.style.display = match ? '' : 'none';
      });
    });
  }

  function injectStyles() {
    const style = document.createElement('style');
    style.textContent = `
      .workshop-search-bar {
        position: fixed;
        top: 20px;
        left: 50%;
        transform: translateX(-50%);
        width: 300px;
        padding: 8px 12px;
        font-size: 16px;
        border: 1px solid #ccc;
        border-radius: 6px;
        z-index: 9999;
        background: #fff;
        box-shadow: 0 2px 6px rgba(0,0,0,0.2);
      }
    `;
    document.head.appendChild(style);
  }

  waitForWorkshop();
})();
