(function () {
  function waitForWorkshop() {
    const interval = setInterval(() => {
      const modCards = document.querySelectorAll('[data-mod]');
      if (modCards.length > 0) {
        clearInterval(interval);
        console.log('Workshop mods detected:', modCards);
        injectStyles();
        createSearchBar(modCards);
      }
    }, 500);
  }

  function createSearchBar(modCards) {
    const bar = document.createElement('input');
    bar.type = 'text';
    bar.placeholder = '🔍 Search mods...';
    bar.className = 'workshop-search-bar';
    document.body.appendChild(bar);

    bar.addEventListener('input', () => {
      const query = bar.value.toLowerCase();

      modCards.forEach(card => {
        const text = card.textContent.toLowerCase();
        const match = text.includes(query);
        card.style.display = match ? '' : 'none';
        console.log('Matching mod:', text, '→', match);
      });
    });
  }

  function injectStyles() {
    const style = document.createElement('style');
    style.textContent = `
      .workshop-search-bar {
        position: fixed;
        top: 20px;
        left: 50%;
        transform: translateX(-50%);
        width: 300px;
        padding: 8px 12px;
        font-size: 16px;
        border: 1px solid #ccc;
        border-radius: 6px;
        z-index: 9999;
        background: #fff;
        box-shadow: 0 2px 6px rgba(0,0,0,0.2);
      }
    `;
    document.head.appendChild(style);
  }

  waitForWorkshop();
})();
